import React, {useState} from 'react';

import CartContext from "./cart-context";

const CartProvider = (props) => {
    const [items, updateItem] = useState([])
    const addItemToCartHandler = (newItem) => {
        const existingItemId = items.findIndex(item => item.id === newItem.id)

        const existingCartItem = items[existingItemId];

        if(existingCartItem) {
           for(let i = 0; i<items.length; i++) {
            if(items[i] === existingCartItem) {
                // items[i].quantity += existingCartItem.quantity
                // console.log(items.quantity)
                updateItem([items[i]])
            }
           }
        } else {

        // console.log(existingCartItem);
        updateItem([...items, newItem])
        }
        
        
    }

    const removeItemToCartHandler = (id) => {}

    const cartContext = {
        items: items,
        totalAmount: 0,
        addItem: addItemToCartHandler,
        removeItem: removeItemToCartHandler

    }

    return <CartContext.Provider value={cartContext}>
        {props.children}
    </CartContext.Provider>
}

export default CartProvider;
